package tests_ui;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import org.openqa.selenium.Keys;import ui_pages.CatalogPage;import ui_pages.CitiesPage;import static com.codeborne.selenide.Selenide.*;import static ui_pages.Data.*;public class CityTest extends BaseUITest {    private final CitiesPage city = new CitiesPage();    private final CatalogPage catalog = new CatalogPage();    @Test    @DisplayName("Сохранение состояния корзины при смене города")    void keepCartWhenCityChangesTest() {        city.open()            .selectCity(VOLOGDA_CITY_NAME.getValue())            .checkCity(VOLOGDA_CITY_NAME.getValue());        refresh();        city.checkCity(VOLOGDA_CITY_NAME.getValue())                .chooseDelivery()                .selectDeliveryAddress()                .checkDeliveryCity(VOLOGDA_CITY_NAME.getValue())                .closeDeliveryWindow()                .chooseTakeaway()                .checkTakeawayCity(VOLOGDA_CITY_NAME.getValue())                .selectTakeawayAddress();        catalog.goToPizzaSection()                .scrollAndSelectItem(PIZZA_NAME.getValue())                .selectLargePizza(LARGE_SIZE_TEXT.getValue())                .addToCart(ADD_TO_CART_BUTTON_TEXT.getValue());        actions().sendKeys(Keys.ESCAPE).perform();        city.chooseLocation()                .selectAlternativeCity(CHEREPOVETS_CITY_NAME.getValue())                .checkCartItems();    }}